mapper.select:
select * from device_ where 1=1

  AND company_name LIKE '%${companyName,jdbcType=VARCHAR}'


  AND device_name LIKE '%${deviceName,jdbcType=VARCHAR}'


  AND device_number LIKE '%${deviceNumber,jdbcType=VARCHAR}'


  AND is_online = #{isOnline,jdbcType=VARCHAR}


  AND active_status = #{activeStatus,jdbcType=VARCHAR}
