mapper.select:
SELECT W.super_cmd_id, action_code, COALESCE (send_time, '') AS send_time, COALESCE (status, '') AS status, COALESCE (device_name, '') AS device_name, COALESCE (employee_name, '') AS employee_name FROM ( SELECT C .*, D.employee_name FROM ( SELECT A .*, B.super_cmd_id, action_code, operate_employee_id, send_time, status FROM ( SELECT device_id, device_name FROM device_ WHERE company_id = #{companyId} ) A INNER JOIN door_cmd B ON A .device_id = B.device_id AND B.action_code IN ('3003', '3004', '3005') AND status NOT IN ('4', '5', '6') ) C LEFT JOIN employee_ D ON C .operate_employee_id = D.employee_id AND D.employee_company_id = #{companyId} ) W
W.action_code = #{operateCommand}
and W.device_name like #{deviceName}
ORDER BY W.send_time DESC

mapper.delete:
delete from door_cmd where device_id = #{deviceId}
WHERE
action_code IN ('3003', '3004', '3005')
