mapper.update:
update door_employee
set employee_name = #{employeeName,jdbcType=VARCHAR},
  door_id = #{doorId,jdbcType=VARCHAR},
  door_name = #{doorName,jdbcType=VARCHAR}
where employee_id = #{employeeId,jdbcType=VARCHAR}

mapper.select:
SELECT * FROM door_employee
WHERE 1=1

  AND door_id = #{doorId,jdbcType=VARCHAR}


  AND door_name = #{doorName,jdbcType=VARCHAR}


  AND employee_id = #{employeeId,jdbcType=VARCHAR}


  AND employee_name = #{employeeName,jdbcType=VARCHAR}

select device_id,max(send_time) sendtime from door_cmd where device_id in (select device_id from door_
door_name like '%一号门%'
) group by device_id

select A.employee_id,employee_name,employee_department_name,B.day_of_week,range_start_time,range_end_time,range_door_open_type opentype,C.device_id
from employee_ A,time_range_common_employee B,door_ C where A.employee_id in(select employee_id from door_employee
where door_id = #{relateEmpPermissionCondition.doorId}) and A.employee_id = B.employee_id and C.door_id =  #{relateEmpPermissionCondition.doorId}


    and A.employee_name like #{relateEmpPermissionCondition.empName}


    and A.employee_department_name like #{relateEmpPermissionCondition.deptName}


    and B.range_door_open_type = #{relateEmpPermissionCondition.openType}
