mapper.update:
update door_exception
set alarm_type = #{alarmType,jdbcType=VARCHAR},
  alarm_date = #{alarmDate,jdbcType=VARCHAR},
  employee_id = #{employeeId,jdbcType=VARCHAR}
where door_exception_id = #{doorExceptionId,jdbcType=VARCHAR}

mapper.select:
SELECT COALESCE (S.record_type, '') AS record_type, COALESCE (record_date, '') AS record_date, COALESCE (employee_name, '') AS employee_name, COALESCE ( employee_department_name, '' ) AS employee_department_name, COALESCE (device_name, '') AS device_name
FROM ( SELECT N.*, D.device_name,company_id FROM ( SELECT X.*, Y.device_id FROM ( SELECT A .record_type, record_date, door_id, B.employee_name, employee_department_name FROM door_record A LEFT JOIN employee_ B ON A .employee_id = B.employee_id
AND B.employee_company_id = #{doorRecordCondition.companyId}
AND A .employee_id IN ( SELECT employee_id FROM employee_
employee_company_id = #{doorRecordCondition.companyId}
) WHERE A .record_type IN ( '1', '20', '25', '27', '28', '30', '32') ) X LEFT JOIN door_ Y ON X.door_id = Y.door_id ) N LEFT JOIN device_ D ON N.device_id = D.device_id ) S
S.employee_name like #{doorRecordCondition.name}
and S.employee_department_name like #{doorRecordCondition.department}
and S.company_id = #{doorRecordCondition.companyId}
and S.record_type = #{doorRecordCondition.punchCardType}
and S.record_date like #{doorRecordCondition.punchCardTime}
and S.device_name like #{doorRecordCondition.deviceName}
order by S.record_date DESC
