mapper.update:
update door_
set door_name = #{doorName,jdbcType=VARCHAR},
  device_id = #{deviceId,jdbcType=VARCHAR},
  operate_time = #{operateTime,jdbcType=VARCHAR},
  operate_employee = #{operateEmployee,jdbcType=VARCHAR}
where door_id = #{doorId,jdbcType=VARCHAR}

mapper.select:
SELECT W.door_id, COALESCE (door_name, '') AS door_name, COALESCE (operate_time, '') AS operate_time, device_id, company_id, COALESCE (device_name, '') AS device_name, operate_employee AS employee_id, COALESCE (employee_name, '') AS employee_name FROM ( SELECT C .*, D.employee_name FROM ( SELECT A .*, B.door_id, door_name, operate_time, operate_employee FROM ( SELECT device_id, device_name, company_id FROM device_ WHERE company_id = #{companyId} ) A INNER JOIN door_ B ON A .device_id = B.device_id ) C LEFT JOIN employee_ D ON C .company_id = D.employee_company_id AND C .operate_employee = D.employee_id ) W
and W.door_name like #{doorName}
order by W.operate_time desc
